function FaqRow({ q, a }: FaqItem) {
  const [open, setOpen] = useState(false);
  return (
    <li>
      <button
        className="w-full py-4 text-left flex items-center justify-between gap-4"
        onClick={() => setOpen((v) => !v)}
        aria-expanded={open}
      >
        <span className="text-lg md:text-xl font-medium">{q}</span>
        <span
          className="shrink-0 transition-transform duration-200 w-6 h-6 rounded-full border border-black/70 grid place-items-center"
          aria-hidden
        >
          {open ? <CloseIcon fontSize="small" /> : <AddIcon fontSize="small" />}
        </span>
      </button>

      {/* Answer */}
      {open && <div className="pb-4 -mt-1 text-black/70">{a}</div>}
    </li>
  );
}

function IconBtn({
  children,
  label,
}: {
  children: React.ReactNode;
  label: string;
}) {
  return (
    <a
      href="#"
      aria-label={label}
      className="rounded-xl bg-[#CDBCEC] text-black  p-3 "
    >
      {children}
    </a>
  );
}

